/* only return new items */
const staticData = getWorkflowStaticData('global');
const lastItemId = staticData.lastItemId;

console.log('lastItemId', lastItemId);
const firstItem = items[0];
let newItems = [];

function getId(item) {
  return item.json.guid;
}

if (lastItemId) {
  for (const item of items) {
    if (getId(item) === lastItemId) {
      break;
    }
    newItems.push(item)
  }
} else {
  newItems = [firstItem]
}

staticData.lastItemId = getId(firstItem)
return newItems.reverse();

/* filter items */
const newItems = [];

const regexes = {
  'star': /^pseudoyu starred/,
  'pull-request': /^pseudoyu opened a pull request/,
  'issue': /^pseudoyu opened an issue/,
}
const tagSymbols = {
  'star': 'üåü',
  'pull-request': '‚§¥Ô∏è',
  'issue': '‚ö°Ô∏è'
}

for (const item of items) {
  const title = item.json.title
  let tag = ''

  for (const key in regexes) {
    const regex = regexes[key]
    if (regex.test(title)) {
      tag = key
      break
    }
  }
  if (tag !== '') {
    item.json.tag = tag
    item.json.tgTitle = `${tagSymbols[tag]} ${title.replace(/^pseudoyu /, '')} #github #${tag}`
    item.json.guid = item.json.id
    newItems.push(item)
  }
}

return newItems
